#Requires -version 2.0Function Get-Weather { Param(  [Parameter(Mandatory=$true)]  [string]$city,  [Parameter(Mandatory=$true)]  [string]$country )#end param $URI = "http://www.webservicex.net/globalweather.asmx?wsdl" $Proxy = New-WebServiceProxy -uri $URI -namespace WebServiceProxy $Proxy.GetWeather($city,$country)} #end Get-Weather[xml]$xml = Get-Weather -city "sofia" -country "Bulgaria"$xml.CurrentWeather